    #[doc = "   <li>scale_ms: 100 milliseconds</li>"]
    #[doc = "   <li>retry_period_ms: 10,000 milliseconds (10 seconds)</li>"]
    #[doc = "   <li>update_rate_ms: 100 milliseconds</li>"]
    #[doc = "   <li>min_measured: 50</li>"]
